package com.example.theoldnerds;import android.media.Image;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.RadioButton;import android.widget.RadioGroup;import android.widget.TextView;import android.widget.Toast;import androidx.appcompat.app.AppCompatActivity;public class addComics extends AppCompatActivity implements View.OnClickListener {    EditText comicName, comicDescrip, comicDate;    Image comicImg;    RadioGroup radioGroup;    Button addComic;    TextView textView;    RadioButton radioButton;    String comicCat;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_add_comics);        getSupportActionBar().setDisplayHomeAsUpEnabled(true);        comicName = (EditText)findViewById(R.id.etComicName);        comicDescrip = (EditText)findViewById(R.id.etComicDescription);        comicDate = (EditText) findViewById(R.id.etComicDateOfAcq);        addComic = (Button) findViewById(R.id.btnAddItem);        radioGroup = findViewById(R.id.radioGroup);        textView = findViewById(R.id.txtVCat2);        addComic.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                if (!(comicName.length()==0 && !(comicDescrip.length()==0) && !(comicDate.length()==0))){                   int radii = radioGroup.getCheckedRadioButtonId();                    radioButton = (RadioButton)findViewById(radii);                    textView.setText("Category: " + radioButton.getText());                    comicCat = (String) radioButton.getText();                    MyDatabase myDB = new MyDatabase(addComics.this);                    myDB.addComic(comicName.getText().toString().trim(),                            comicDescrip.getText().toString().trim(),                            comicDate.getText().toString().trim(),                            comicCat.toString().trim());                }                else{                    Toast.makeText(addComics.this, "Values cannot be empty!", Toast.LENGTH_SHORT).show();                }            }        });    }    @Override    public void onClick(View v) {    }}